// src/components/ProfessoresList.js

import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import ProfessorItem from './ProfessorItem';
import ProfessorForm from './ProfessorForm';
import ConfirmationModal from './ConfirmationModal';
import { getProfessores, createProfessor, updateProfessor, deleteProfessor } from '../api/professoresAPI';

Modal.setAppElement('#root'); // Isso é necessário para acessibilidade

const ProfessoresList = () => {
    const [professores, setProfessores] = useState([]);
    const [editingProfessor, setEditingProfessor] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isConfirmModalOpen, setIsConfirmModalOpen] = useState(false);
    const [professorToDelete, setProfessorToDelete] = useState(null);


    useEffect(() => {
        const fetchData = async () => {
            const data = await getProfessores();
            setProfessores(data);
        };

        fetchData();
    }, []);

    const handleAddOrUpdate = async (professor) => {
        if (editingProfessor) {
            const updatedProfessor = await updateProfessor(editingProfessor.id, professor);
            setProfessores(professores.map(p => p.id === editingProfessor.id ? updatedProfessor : p));
            setEditingProfessor(null);
        } else {
            const newProfessor = await createProfessor(professor);
            if (newProfessor) {
                setProfessores([...professores, newProfessor]);
            }
        }
        setIsModalOpen(false);
    };

    const handleDelete = async (id) => {
        //const confirmDelete = window.confirm("Você tem certeza de que deseja excluir este professor?");
        //if (confirmDelete) {
        //    await deleteProfessor(id);
        //    setProfessores(professores.filter(p => p.id !== id));
        //}

        if (professorToDelete) {
            await deleteProfessor(professorToDelete);
            setProfessores(professores.filter(p => p.id !== professorToDelete));
            setProfessorToDelete(null);  // Reset the ID after deleting
        }
        setIsConfirmModalOpen(false);  // Close the confirmation modal

    };

    const showDeleteConfirmation = (id) => {
        setProfessorToDelete(id);  // Store the ID of the professor to be deleted
        setIsConfirmModalOpen(true);  // Open the confirmation modal
    };

    const handleEdit = (professor) => {
        setEditingProfessor(professor);
        setIsModalOpen(true);
    };

    return (
        <div className="professores-list">
            <h2>Professores</h2>
            <button onClick={() => setIsModalOpen(true)}>Adicionar</button>
            <Modal 
                isOpen={isModalOpen}
                onRequestClose={() => setIsModalOpen(false)}
                contentLabel="Adicionar Professor"
            >
                <ProfessorForm
                    onSubmit={handleAddOrUpdate}
                    professor={editingProfessor}
                />
                <button onClick={() => setIsModalOpen(false)}>Fechar</button>
            </Modal>

            <ConfirmationModal
            isOpen={isConfirmModalOpen}
            onConfirm={handleDelete}
            onCancel={() => setIsConfirmModalOpen(false)}
            message="Tem certeza de que deseja excluir este professor?"
        />

            {professores.map(professor => (
                <ProfessorItem
                    key={professor.id}
                    professor={professor}
                    onDelete={handleDelete}
                    onEdit={handleEdit}
                />
            ))}
        </div>
    );
}

export default ProfessoresList;
