import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import ProfessorTable from "./ProfessorTable";
import ProfessorForm from "./ProfessorForm";
import { getProfessores, createProfessor, updateProfessor, deleteProfessor } from "../api/professoresAPI";
import "./CadastroProfessores.css";

Modal.setAppElement("#root"); 

const CadastroProfessores = () => {
    const [professores, setProfessores] = useState([]);
    const [editingProfessor, setEditingProfessor] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            const data = await getProfessores();
            setProfessores(data);
        };

        fetchData();
    }, []);

    const handleAddOrUpdate = async (professor) => {
        if (editingProfessor) {
            const updatedProfessor = await updateProfessor(
              editingProfessor.cpf,
              professor
            );
            setProfessores(
              professores.map((p) =>
                p.cpf === editingProfessor.cpf ? updatedProfessor : p
              )
            );
            setEditingProfessor(null);
          } else {
            const newProfessor = await createProfessor(professor);
            if (newProfessor) {
              setProfessores([...professores, newProfessor]);
            }
          }
          setIsModalOpen(false);
    };

    const handleDelete = async (cpf) => {
        // ... (mantenha o c贸digo original aqui)
    };

    const handleEdit = (professor) => {
        // ... (mantenha o c贸digo original aqui)
    };

    return (
        <div className="cadastro-professores">
            <h1 className="title">Cadastro de Professores</h1>

            <button className="add-btn" onClick={() => setIsModalOpen(true)}>Adicionar Professor</button>

            <Modal
                isOpen={isModalOpen}
                onRequestClose={() => setIsModalOpen(false)}
                contentLabel="Adicionar Professor"
            >
                <ProfessorForm
                    onSubmit={handleAddOrUpdate}
                    professor={editingProfessor}
                />
                <button className="close-btn" onClick={() => setIsModalOpen(false)}>Fechar</button>
            </Modal>

            <ProfessorTable 
                professores={professores}
                onEdit={handleEdit}
                onDelete={handleDelete}
            />
        </div>
    );
};

export default CadastroProfessores;
